jQuery(document).ready(function($) {
    
	$('.site-container').prepend('<nav id="mobile-nav"><ul></ul></nav>');
    $('.menu-primary > li, .menu-secondary > li').clone().appendTo('#mobile-nav ul');
    $('#mobile-nav').mmenu();
	
});